      }

      .matrix-table {
        max-width: 45em;
      }

      .matrix-table thead th {
        padding: 0 0 7px !important;
        line-height: 1em;
      }
      .matrix-table tbody th {
        padding: 0;
        vertical-align: middle;
      }

      .matrix-table tbody td {
        padding: 0.5em 0;
