  protected function print_matrix( $field, $value = array() ) {
    ?>
    <table class="form-table matrix-table" id="field_matrix_<?php echo esc_attr( $field['id'] ); ?>">
      <thead>
        <tr>
          <th></th>
          <?php
          foreach ( (array) $field['columns'] as $column ) {
            if ( empty( $column['label'] ) ) {
              continue;
            }
            ?>
            <th>
              <?php echo esc_html( $column['label'] ); ?>
            </th>
            <?php
          }
          ?>
        </tr>
      </thead>
      <tbody>
        <?php
        if ( in_array( 'order', array_keys( $field['columns'] ) ) && $value ) {
          uksort($field['rows'], function ($row1, $row2) use ($value) {
            if ($value[$row1]['order'] == $value[$row2]['order']) return 0;
            return $value[$row1]['order'] < $value[$row2]['order'] ? -1 : 1;
          });
        }

        foreach ( (array) $field['rows'] as $row_id => $row ) {
          ?>
          <tr>
            <th>
              <?php echo esc_html( $row['label'] ); ?>
            </th>
            <?php foreach ( (array) $field['columns'] as $column_id => $column ) {
              if ( ! empty( $column['rows'] ) && ! in_array( $row_id, $column['rows'] ) ) {
                ?>
                <td></td>
                <?php
                continue;
              }

              if ( $column['type'] !== 'hidden' ) { ?>
              <td>
                <?php }
                $name = sprintf( '%s[%s][%s]', $field['uid'], $column_id, $row_id );
                switch ( $column['type'] ){
                case 'text':
                case 'number':
                  $input_value = $value[$row_id][$column_id];
                  printf( '<input name="%1$s" id="%1$s" type="%2$s" placeholder="%3$s" value="%4$s" />', $name, $column['type'], $row['default'][$column_id], $input_value );
                  break;
                case 'radio':
                  $name = sprintf( '%s[%s]', $field['uid'], $column_id );
                  printf( '<input name="%1$s" id="%1$s" type="radio" value="%2$s" %3$s />', $name, $row_id, checked( $value[$row_id][$column_id], true, false ) );
                  break;
                case 'checkbox':
                  printf( '<input name="%1$s" id="%1$s" type="checkbox" %2$s />', $name, checked( $value[$row_id][$column_id], true, false ) );
                  break;
                case 'hidden':
                  if ( $column['editable'] ) {
                    printf( '<input name="%1$s" id="%1$s" type="hidden" value="%2$s" />', $name, $value[$row_id][$column_id] );
                  }
                }
              
                if ( $column['type'] !== 'hidden' ) { ?>
              </td>
              <?php }
            } ?>
          </tr>
          <?php
        } ?> 
      </tbody>
    </table>
    <?php
  }

  public function get_field( $key ) {
    $default = null;
    if ( isset( $this->fields[$key] ) && $this->fields[$key]['default'] ) {
      $default = $this->fields[$key]['default'];
    }

    $field = get_option('ivrita_' . $key, $default);

    return $field;
  }

  public function get_matrix( $field_name ) {
    $value = (array) $this->get_field( $field_name );
    $field = $this->fields[ $field_name ];
    
    $matrix = array();
    foreach ( $field['rows'] as $row_id => $row ) {
      $matrix[ $row_id ] = array();
      foreach ( $field['columns'] as $column_id => $column ) {
        $col_value = null;

        if ( in_array( $column['type'], array( 'text', 'hidden' ) ) ) {
          if ( !empty( $value[ $column_id ][ $row_id ] ) ) {
            $col_value = $value[ $column_id ][ $row_id ];
          } else if ( isset( $row['default'] ) && !empty( $row['default'][ $column_id ] ) ) {
            $col_value = $row['default'][ $column_id ];
          }
        } else if ( in_array( $column['type'], array( 'radio' ) )) {
          if ( ! empty( $value[ $column_id ] ) ) {
            $col_value = !empty($value[ $column_id ]) && $value[ $column_id ] === $row_id;
          } else {
            $col_value = !empty($column['default']) && $column['default'] === $row_id;
          }
        } else if ( in_array( $column['type'], array( 'checkbox' ) )) {
          if ( ! empty( $value[ $column_id ] ) ) {
            $col_value = !empty($value[ $column_id ][ $row_id ]) && $value[ $column_id ][ $row_id ] === 'on';
          } else {
            $col_value = !empty($row['default'][ $column_id ]) && $row['default'][ $column_id ] === 'on';
          }
        }

        $matrix[ $row_id ][ $column_id ] = $col_value;
      }
    }

    if ( in_array( 'order', array_keys( $field['columns'] ) ) ) {
      uasort($matrix, function ($row1, $row2) {
        if ($row1['order'] == $row2['order']) return 0;
        return $row1['order'] < $row2['order'] ? -1 : 1;
      });
    }

    return $matrix;
  }

  public function get_post_field( $field_key, $post_id = null ) {
    if ( $post_id === null ) {
      global $post;
      $post_id = $post->ID;
    }
    return get_post_meta( $post_id, '_ivrita_post_' . $field_key, true );
  }

  public function enqueue_admin_scripts( $hook_suffix ) {
    if ( $hook_suffix != 'toplevel_page_ivrita' ) {
      return;
    }
    
    wp_enqueue_script( 'jquery-ui-sortable' );
  }

  public function add_meta_boxes() {
    add_meta_box(
      'ivrita-post-settings',
      __( 'Ivrita Per-Post Settings', 'ivrita' ),
      array( $this, 'per_post_settings' ),
      array( 'post', 'page' ),
      'side'
    );
  }

